SELECT TC.ECount_CD Customer_EID, TC.Customer_ID,Customer_NM, WB.Product_ID, PP.ECount_CD, PP.ECount_CDS,WB.Product_Id, PT.Product_Nm, WB.Product_Price_Seq,PP.Product_Capa,ECount_Spec, ECount_User_CD Sales_Nm, WB.Bottle_Sale_YN, WB.Order_Count, WB.Product_Price, WB.Order_Count*WB.Product_Price Supply_Price,WB.Order_Count*WB.Product_Price*0.1 VAT,Bottle_Work_Cd,GAS_CD,Agency_Yn FROM ( SELECT count(Work_Seq) Order_Count,TB.Work_Report_Seq,Product_ID, Product_Price_Seq,TB.Create_ID,Bottle_Sale_YN, Product_Price, Bottle_Work_Cd,GAS_CD FROM TB_Work_Bottle TB, TB_Work_Report TW WHERE Bottle_Work_Cd in ('0308','0309','0314', '0316') AND TB.Work_Report_Seq = TW.Work_Report_Seq AND DATE_FORMAT(Work_Dt,'%Y/%m/%d')= #{searchDt}
AND TB.Create_ID = #{searchUserId}
Group By Work_Report_Seq,Product_ID, Product_Price_Seq ,TB.Create_ID,Bottle_Sale_YN, Product_Price, Bottle_Work_Cd,GAS_CD ) WB, TB_Work_Report WR ,TB_Product PT, TB_Product_Price PP, TB_Customer TC, TB_User TU WHERE WB.Work_Report_Seq = WR.Work_Report_Seq AND WR.Delete_YN = 'N' AND WB.Product_ID = PT.Product_ID AND WB.Product_ID = PP.Product_ID AND WB.Product_Price_Seq = PP.Product_Price_Seq AND WR.Customer_ID = TC.Customer_ID AND WB.Create_ID = TU.User_Id Order By WB.Work_Report_Seq desc

SELECT count(Work_Seq) Order_Count,TB.Work_Report_Seq,Product_ID, Product_Price_Seq, Bottle_Work_Cd,TB.Customer_ID FROM TB_Work_Bottle TB, TB_Work_Report TW WHERE Bottle_Work_Cd in ('0310','0315') AND TB.Work_Report_Seq = TW.Work_Report_Seq AND DATE_FORMAT(Work_Dt,'%Y/%m/%d')= #{searchDt}
AND TB.Create_ID = #{searchUserId}
AND TB.Bottle_Type='F' Group By Work_Report_Seq,Product_ID, Product_Price_Seq ,Bottle_Work_Cd,TB.Customer_ID
