mapper.select:
SELECT
FROM TB_Daily_Stat_Customer WHERE Customer_Id = #{searchCustomerId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC

SELECT
FROM TB_Monthly_Stat_Customer WHERE Customer_Id = #{searchCustomerId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC

SELECT wb.Work_Report_Seq, wb.Create_Id, User_Nm Sales_Nm,wb.Customer_Id,Customer_Nm,wb.Product_ID,wb.Product_Price_Seq,Product_Nm, case when wb.Gas_ID > 0 and instr(pp.Product_Capa,'Kg') <=0 then concat(pp.Product_Capa,'L') else pp.Product_Capa end Product_Capa,Work_Etc,DATE_FORMAT(wb.Create_Dt,'%Y/%m/%d') SALE_Dt, count(case when Bottle_Work_Cd in ('0309','0314','0316') then 1 end) as Rent_Count, count(case when Bottle_Work_Cd in ('0310','0315','0317') then 1 end) as Back_Count, count(case when Bottle_Work_Cd='0308' then 1 end) as Sale_Count, count(case when Bottle_Work_Cd in ('0311','0312','0313') then 1 end) as As_Count, count(case when Bottle_Work_Cd = '0318' then 1 end) as Charge_Count, count(case when Bottle_Work_Cd = '0306' then 1 end) as Out_Count, count(case when Bottle_Work_Cd = '0301' then 1 end) as In_Count FROM TB_Work_Bottle wb, TB_Customer tc ,TB_Product tp, TB_Product_Price pp , TB_User tu , TB_Work_Report wr WHERE DATE_FORMAT(wb.Create_Dt,'%Y/%m/%d') between #{searchStatDtFrom} AND #{searchStatDtEnd} AND Bottle_Work_Cd in ('0309','0314','0316','0310','0315','0317','0308','0311','0312','0313','0318' ,'0301','0306' ) AND wb.Customer_ID = tc.Customer_ID
AND wb.Customer_ID = #{paramCustomerId}
AND wb.Product_ID = tp.Product_ID AND wb.Product_ID = pp.Product_ID AND wb.Product_Price_Seq = pp. Product_Price_Seq AND wb.Create_ID = tu.User_ID AND wb.Work_Report_Seq = wr.Work_Report_Seq GROUP By wb.Customer_Id,wb.Product_ID,wb.Product_Price_Seq,wb.Create_Id Order By wb.Work_Report_Seq desc

SELECT wb.Create_Id, User_Nm Sales_Nm,wb.Customer_Id,Customer_Nm,wb.Product_ID,wb.Product_Price_Seq,Product_Nm,Work_Etc,DATE_FORMAT(wb.Create_Dt,'%Y/%m/%d') SALE_Dt, case when wb.Gas_ID > 0 and instr(pp.Product_Capa,'Kg') <=0 then concat(pp.Product_Capa,'L') else pp.Product_Capa end Product_Capa, count(case when Bottle_Work_Cd in ('0309','0314','0316') then 1 end) as Rent_Count, count(case when Bottle_Work_Cd in ('0310','0315','0317') then 1 end) as Back_Count, count(case when Bottle_Work_Cd='0308' then 1 end) as Sale_Count, count(case when Bottle_Work_Cd in ('0311','0312','0313') then 1 end) as As_Count, count(case when Bottle_Work_Cd = '0318' then 1 end) as Charge_Count FROM TB_Work_Bottle wb, TB_Customer tc ,TB_Product tp, TB_Product_Price pp , TB_User tu , TB_Work_Report wr WHERE DATE_FORMAT(wb.Create_Dt,'%Y/%m/%d') between #{searchStatDtFrom} AND #{searchStatDtEnd} AND Bottle_Work_Cd in ('0309','0314','0316','0310','0315','0317','0308','0311','0312','0313','0318' ) AND wb.Customer_ID = tc.Customer_ID
AND wb.Customer_ID = #{paramCustomerId}
AND wb.Product_ID = tp.Product_ID AND wb.Product_ID = pp.Product_ID AND wb.Product_Price_Seq = pp. Product_Price_Seq AND wb.Create_ID = tu.User_ID AND wb.Work_Report_Seq = wr.Work_Report_Seq GROUP By wb.Customer_Id,wb.Product_ID,wb.Product_Price_Seq,wb.Create_Id Order By wb.Work_Report_Seq desc

SELECT Customer_Id, count(distinct concat(wb.Product_ID,'-',wb.Product_Price_Seq)) ORDER_Count FROM TB_Work_Bottle wb WHERE DATE_FORMAT(wb.Create_Dt,'%Y/%m/%d') between #{searchStatDtFrom} AND #{searchStatDtEnd} AND Bottle_Work_Cd in ('0309','0314','0316','0310','0315','0317','0308','0311','0312','0313','0318' )
AND wb.Customer_ID = #{paramCustomerId}
GROUP By wb.Customer_id Order By wb.Work_Report_Seq desc
